# ─────────────────────────────────────────────────────────────────────────────
# Cell 4 – Query the Azure Cost Management API
# ─────────────────────────────────────────────────────────────────────────────

def query_cost_by_resource(
    access_token: str,
    scope: str,
    start_date: str,
    end_date: str
) -> dict:
    """
    Call the Cost Management /query endpoint and return the raw JSON response.

    The query groups costs by:
      - ServiceName  (e.g. Azure Synapse Analytics, Azure Storage, etc.)
      - ResourceId   (unique ARM resource identifier)
      - ResourceType (e.g. Microsoft.Synapse/workspaces)
    """
    url = (
        f"https://management.azure.com/{scope}"
        f"/providers/Microsoft.CostManagement/query"
        f"?api-version={API_VERSION}"
    )

    headers = {
        "Authorization": f"Bearer {access_token}",
        "Content-Type": "application/json"
    }

    payload = {
        "type": "ActualCost",
        "timeframe": "Custom",
        "timePeriod": {
            "from": f"{start_date}T00:00:00+00:00",
            "to":   f"{end_date}T23:59:59+00:00"
        },
        "dataSet": {
            "granularity": "None",
            "aggregation": {
                "totalCost": {
                    "name": "Cost",
                    "function": "Sum"
                },
                "totalCostUSD": {
                    "name": "CostUSD",
                    "function": "Sum"
                }
            },
            "grouping": [
                {"type": "Dimension", "name": "ServiceName"},
                {"type": "Dimension", "name": "ResourceId"},
                {"type": "Dimension", "name": "ResourceType"}
            ]
        }
    }

    resp = requests.post(url, headers=headers, json=payload, timeout=60)
    if resp.status_code != 200:
        print(f"API error {resp.status_code}: {resp.text}")
    resp.raise_for_status()
    return resp.json()


# Build date range
now      = datetime.now(timezone.utc)
end_dt   = now.strftime("%Y-%m-%d")
start_dt = (now - timedelta(days=LOOKBACK_DAYS)).strftime("%Y-%m-%d")

print(f"Querying costs from {start_dt} to {end_dt} ...")
cost_response = query_cost_by_resource(access_token, SCOPE, start_dt, end_dt)
print("Query completed successfully.")
